mapper.select:
select
id,entity_id,view_id,name,data_type,is_primary,is_foreign,is_delete,gmt_create,gmt_modified
from attribute


        and id = #{ID,jdbcType=BIGINT}


        and entity_id = #{entityID,jdbcType=BIGINT}


        and view_id = #{viewID,jdbcType=BIGINT}


        and name = #{name,jdbcType=VARCHAR}


        and data_type = #{dataType,jdbcType=VARCHAR}


        and is_primary = #{isPrimary,jdbcType=TINYINT}


        and is_foreign = #{isForeign,jdbcType=TINYINT}


        and gmt_create = #{gmtCreate,jdbcType=DATETIME}


        and gmt_modified = #{gmtModified,jdbcType=DATETIME}

    and is_delete = 0
