mapper.select:
select
id,entity_id,view_id,name,data_type,is_primary,is_foreign,is_delete,gmt_create,gmt_modefied
from attributeDO


        and id = #{id,jdbcType=BIGINT}


        and entity_id = #{entity_id,jdbcType=BIGINT}


        and view_id = #{view_id,jdbcType=BIGINT}


        and name = #{name,jdbcType=VARCHAR}


        and data_type = #{data_type,jdbcType=VARCHAR}


        and is_primary = #{is_primary,jdbcType=TINYINT}


        and is_foreign = #{is_foreign,jdbcType=TINYINT}


        and is_delete = #{is_delete,jdbcType=TINYINT}


        and gmt_create = #{gmt_create,jdbcType=DATETIME}


        and gmt_modefied = #{gmt_modefied,jdbcType=DATETIME}
