mapper.select:
select
id, related_obj_id,layout_x,layout_y,width,height,is_delete,gmt_create,gmt_modified
from graph_info


        and id = #{id,jdbcType=BIGINT}


        and related_obj_id = #{relatedObjId,jdbcType=BIGINT}


        and layout_x = #{layoutX,jdbcType=NUMERIC}


        and layout_y = #{layoutY,jdbcType=NUMERIC}


        and width = #{width,jdbcType=NUMERIC}


        and height = #{height,jdbcType=NUMERIC}

    and is_delete = 0

mapper.insert:
insert into graph_info


        id,


        related_obj_id,


        layout_x,


        layout_y,


        width,


        height,




        #{id,jdbcType=BIGINT},


        #{relatedObjId,jdbcType=BIGINT}


        #{layoutX,jdbcType=NUMERIC}


        #{layoutY,jdbcType=NUMERIC}


        #{width,jdbcType=NUMERIC}


        #{height,jdbcType=NUMERIC}
